import { useEffect, useState } from "react";
import { Row } from "react-bootstrap";
import axios from "axios";
import SingleBook from "./book/SingleBook";
import "./css/DashBoard.css";
import { Button } from "react-bootstrap";

function DashBoard() {
  const [bookState, setBookState] = useState({ books: [], bookLoading: true });

  const [searchForm, setSearchForm] = useState({
    search: "",
    category: null,
  });

  const { search, category } = searchForm;
  const onChangeSearchForm = (event) =>
    setSearchForm({ ...searchForm, [event.target.name]: event.target.value });
  const onSubmitSearch = () => {
    searchBooks();
  };
  async function searchBooks() {
    try {
      const params = new URLSearchParams("");
      if (searchForm.category !== null)
        params.set("category", searchForm.category);
      if (searchForm.search && searchForm.search !== "")
        params.set("search", searchForm.search);
      console.log(params.toString());
      // const response = await axios.get("api/book/search?page=1");
      // setBookState({ books: response.data.data, bookLoading: false });
    } catch (error) {
      console.log(error);
    }
  }
  useEffect(() => {
    async function getBooks() {
      try {
        const response = await axios.get("api/book/search?page=1");
        setBookState({ books: response.data.data, bookLoading: false });
      } catch (error) {
        console.log(error);
      }
    }
    getBooks();
  }, []);
  let body;
  console.log("re-rendered");
  if (bookState.bookLoading) {
    body = <div>Loading</div>;
  } else {
    body = (
      <Row>
        {bookState.books.map((book) => {
          return (
            <>
              <SingleBook book={book} />
            </>
          );
        })}
      </Row>
    );
  }

  let searchView = (
    <>
      <ul className="ul-nav-search">
        <li>
          <select
            name="category"
            onChange={onChangeSearchForm}
            value={category}
          >
            <option>Category 1</option>
            <option>Category 2</option>
            <option>Category 3</option>
          </select>
        </li>
        <li>
          <input
            type="text"
            placeholder="search"
            name="search"
            value={search}
            onChange={onChangeSearchForm}
          />
        </li>
        <li>
          <Button onClick={onSubmitSearch}>Search</Button>
        </li>
      </ul>
    </>
  );
  return (
    <div>
      <div className="nav-search">{searchView}</div>
      <div>{body}</div>
    </div>
  );
}

export default DashBoard;
