import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "../css/BookDetail.css";

const BookDetail = () => {
  let { id } = useParams();

  const [bookState, setBookState] = useState({
    book: null,
    bookLoading: true,
  });

  useEffect(() => {
    async function getBookDetail() {
      try {
        const bookResponse = await axios.get(`api/book/${id}`);

        setBookState({
          book: bookResponse.data.data,
          bookLoading: false,
        });
      } catch (error) {
        console.log(error);
      }
    }
    getBookDetail();
  }, []);
  let body;
  if (bookState.bookLoading)
    body = (
      <>
        <div>Loading</div>
      </>
    );
  else
    body = (
      <>
        <div class="product-details-container">
          <div class="product-details-image-left">
            <div class="img-holder">
              <img
                src="https://cdn0.fahasa.com/media/catalog/product/i/m/image_179484.jpg"
                alt=""
              />
            </div>
            <div class="product-buttons">
              <button class="btn-add-to-cart">Thêm vào giỏ hàng</button>
              <button class="btn-buy-now">Mua ngay</button>
            </div>
          </div>
          <div class="product-details-right">
            <div class="product-title">{bookState.book.name}</div>
            <p>
              <strong>Tác giả: </strong>
              {bookState.book.authorname}
            </p>
            <div>
              <strong>Đánh giá (96) </strong>
            </div>
            <div class="rating">
              <input type="radio" name="rating" value="5" id="5" />
              <label for="5">☆</label>
              <input type="radio" name="rating" value="4" id="4" />
              <label for="4">☆</label>
              <input type="radio" name="rating" value="3" id="3" />
              <label for="3">☆</label>
              <input type="radio" name="rating" value="2" id="2" />
              <label for="2">☆</label>
              <input type="radio" name="rating" value="1" id="1" />
              <label for="1">☆</label>
            </div>
            <label for="description">
              <strong>Mô tả:</strong>
            </label>
            <div>
              Truy cập mua sách tại website: https://tiki.vn 3. SmartBook
              (Shopee) Đây không hẳn là một trang bán sách, đây là một gian hàng
              trên Shopee chuyên cung cấp sách. Lúc đầu quyết định mua sách.
              {bookState.book.description}
            </div>
            <div>
              <strong>Giá:</strong> {bookState.book.price} đ
            </div>
            <div>
              <div class="line-quantity">
                <label for="input-quantity">
                  <strong>Số lượng: </strong>
                </label>
                <div class="input-quantity" id="input-quantity">
                  <button>-</button>
                  <input type="number" value="1" />
                  <button>+</button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </>
    );
  return body;
};

export default BookDetail;
