import { useEffect, useState } from "react";
import { Row } from "react-bootstrap";
import axios from "axios";
import SingleBook from "./book/SingleBook";
import "./css/DashBoard.css";
import { Button } from "react-bootstrap";
import BookPaging from "./book/BookPaging";
import * as queryString from "query-string";

function DashBoard() {
  let params = queryString.parse(window.location.search);

  const [bookState, setBookState] = useState({
    books: [],
    bookLoading: true,
    currentPage: 1,
    totalPage: 1,
  });

  const [searchForm, setSearchForm] = useState({
    search: "",
    category: null,
  });
  const [url, setUrl] = useState(window.location);

  const { search, category } = searchForm;
  const onChangeSearchForm = (event) =>
    setSearchForm({ ...searchForm, [event.target.name]: event.target.value });
  const onSubmitSearch = () => {
    searchBooks();
  };
  function setGetParam(params) {
    var newUrl =
      window.location.origin + window.location.pathname + "?" + params;
    window.history.pushState({ path: newUrl }, "", newUrl);
    setUrl(newUrl);
  }
  async function searchBooks() {
    try {
      let reqParams = "page=1&";
      if (search && search.length !== 0) reqParams += "name=" + search;
      if (category) reqParams += "category" + category;

      setGetParam(reqParams);
    } catch (error) {
      console.log(error);
    }
  }

  useEffect(() => {
    async function getBooks() {
      try {
        let reqParams = "";
        if (params && params.name) reqParams += `name=${params.keyword}&`;
        if (params && params.page) reqParams += `page=${params.page}&`;
        if (params && params.category)
          reqParams += `category=${params.category}&`;
        if (reqParams.length === 0) reqParams = "page=1";
        const response = await axios.get(`api/book/search?${reqParams}`);
        setBookState({
          books: response.data.data,
          bookLoading: false,
          currentPage: response.data.currentPage,
          totalPage: response.data.totalPage,
        });
      } catch (error) {
        console.log(error);
      }
    }
    getBooks();
  }, [url]);

  const handlePageChange = (pageNumber) => {
    let key = "page=" + pageNumber;
    setGetParam(key);
  };

  const reloadPage = () => {
    let key = "page=" + 1;
    setGetParam(key);
  };
  let body;

  if (bookState.bookLoading) {
    body = <div>Loading</div>;
  } else {
    body = (
      <>
        <Row>
          {bookState.books.map((book) => {
            return (
              <>
                <SingleBook book={book} />
              </>
            );
          })}
        </Row>
        <BookPaging
          handlePageChange={handlePageChange}
          currentPage={bookState.currentPage}
          totalPage={bookState.totalPage}
        />
      </>
    );
  }

  let searchView = (
    <>
      <ul className="ul-nav-search">
        <li>
          <select
            name="category"
            onChange={onChangeSearchForm}
            value={category}
          >
            <option value={1}>Category 1</option>
            <option value={2}>Category 2</option>
            <option value={3}>Category 3</option>
          </select>
        </li>
        <li>
          <input
            type="text"
            placeholder="search"
            name="search"
            value={search}
            onChange={onChangeSearchForm}
          />
        </li>
        <li>
          <Button onClick={onSubmitSearch}>Search</Button>
        </li>
      </ul>
    </>
  );
  return (
    <div>
      <div className="nav-search">{searchView}</div>
      <div>{body}</div>
    </div>
  );
}

export default DashBoard;
