import React, {useState} from 'react';

import axios from 'axios';
import swal from 'sweetalert';
import { useHistory } from 'react-router-dom';

function Login() {

    const history = useHistory();

    const [loginInput, setLogin] = useState({
        username: '',
        password: '',
        error_list: '',
    });

    const handleInput = (e) => {
        e.persist();
        setLogin({...loginInput, [e.target.name]: e.target.value });
    }

    const loginSubmit = (e) => {
        e.preventDefault();

        const data = {
            username: loginInput.username,
            password: loginInput.password,
        }

        axios.post(`api/login`, data).then(res => {
            if(res.data.success === true)
            {
                localStorage.setItem('auth_token', res.data.data.token);
                localStorage.setItem('auth_name', res.data.data.username);
                swal("Success",res.data.message,"success");
                if(res.data.data.roles[0] === 'admin')
                {
                    history.push('/admin/dashboard');
                }
                else
                {
                    history.push('/');
                }
            }
            else if(res.data.status === 401)
            {
                swal("Warning",res.data.message,"warning");
            }
            else
            {
                setLogin({...loginInput, error_list: res.data.validation_errors });
            }
        });

        // axios.get('/sanctum/csrf-cookie').then(response => {
        //     axios.post(`api/login`, data).then(res => {
        //         if(res.data.status === 200)
        //         {
        //             localStorage.setItem('auth_token', res.data.token);
        //             localStorage.setItem('auth_name', res.data.username);
        //             swal("Success",res.data.message,"success");
        //             if(res.data.role === 'admin')
        //             {
        //                 history.push('/admin/dashboard');
        //             }
        //             else
        //             {
        //                 history.push('/');
        //             }
        //         }
        //         else if(res.data.status === 401)
        //         {
        //             swal("Warning",res.data.message,"warning");
        //         }
        //         else
        //         {
        //             setLogin({...loginInput, error_list: res.data.validation_errors });
        //         }
        //     });
        // });

    }

    return (
        <div>
            <div className="container py-5">
                <div className="row justify-content-center">
                    <div className="col-md-6">
                        <div className="card">
                            <div className="card-header">
                                <h4>Login</h4>
                            </div>
                            <div className="card-body">
                                <form onSubmit={loginSubmit}>
                                    <div className="form-group mb-3">
                                        <label>Username</label>
                                        <input type="email" name="username" onChange={handleInput} value={loginInput.username} className="form-control" />
                                        <span>{loginInput.error_list.username}</span>
                                    </div>
                                    <div className="form-group mb-3">
                                        <label>Password</label>
                                        <input type="password" name="password" onChange={handleInput} value={loginInput.password} className="form-control" />
                                        <span>{loginInput.error_list.password}</span>
                                    </div>
                                    <div className="form-group mb-3">
                                        <button type="submit" className="btn btn-primary">Login</button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Login;
